import React, { Fragment, memo, useEffect, useState } from "react";
import { Dialog, Transition } from "@headlessui/react";
import axios from "axios";
import toast from "react-hot-toast";
import BtnPrimary from "./BtnPrimary";
import BtnSecondary from "./BtnSecondary";

const AddProjectModal = ({
  isModalOpen,
  closeModal,
  edit = false,
  id = null,
}) => {
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");
  const [deadline, setDeadline] = useState("");

  useEffect(() => {
    if (edit && isModalOpen && id) {
      axios
        .get(`http://localhost:9000/project/${id}`)
        .then((res) => {
          const project = res.data[0];
          setTitle(project.title);
          setDesc(project.description);
          setDeadline(project.deadline);
        })
        .catch((error) => {
          toast.error("Failed to fetch project details");
        });
    }
  }, [edit, isModalOpen, id]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const projectData = { title, description: desc, deadline };

    const url = edit
      ? `http://localhost:9000/project/${id}`
      : `http://localhost:9000/project/`;

    const requestMethod = edit ? axios.put : axios.post;

    requestMethod(url, projectData)
      .then((res) => {
        closeModal();
        const customEvent = new CustomEvent("projectUpdate", {
          detail: { ...res.data },
        });
        document.dispatchEvent(customEvent);
        toast.success(
          edit ? "Project updated successfully" : "Project created successfully"
        );
        setTitle("");
        setDesc("");
        setDeadline("");
      })
      .catch((error) => {
        if (error.response && error.response.status === 422) {
          toast.error(error.response.data.message);
        } else {
          toast.error("Something went wrong");
        }
      });
  };

  return (
    <Transition appear show={isModalOpen} as={Fragment}>
      <Dialog
        as='div'
        open={isModalOpen}
        onClose={() => closeModal()}
        className='fixed inset-0 z-50 overflow-y-auto'
      >
        <div className='min-h-screen px-4 text-center'>
          <Dialog.Overlay className='fixed inset-0 bg-black opacity-30' />

          <Transition.Child
            as={Fragment}
            enter='ease-out duration-300'
            enterFrom='opacity-0 scale-95'
            enterTo='opacity-100 scale-100'
            leave='ease-in duration-200'
            leaveFrom='opacity-100 scale-100'
            leaveTo='opacity-0 scale-95'
          >
            <div className='inline-block w-full max-w-2xl p-6 my-8 overflow-hidden text-left align-middle transition-all transform bg-white shadow-xl rounded-2xl'>
              <Dialog.Title
                as='h3'
                className='text-lg font-medium leading-6 text-gray-900'
              >
                {!edit ? "Add Project" : "Edit Project"}
              </Dialog.Title>
              <form onSubmit={handleSubmit} className='mt-4 space-y-4'>
                <div>
                  <label
                    htmlFor='title'
                    className='block text-sm font-medium text-gray-700'
                  >
                    Title
                  </label>
                  <input
                    id='title'
                    name='title'
                    type='text'
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    className='mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm'
                    placeholder='Enter project title'
                  />
                </div>

                <div>
                  <label
                    htmlFor='description'
                    className='block text-sm font-medium text-gray-700'
                  >
                    Description
                  </label>
                  <textarea
                    id='description'
                    name='description'
                    rows='4'
                    value={desc}
                    onChange={(e) => setDesc(e.target.value)}
                    className='mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm'
                    placeholder='Enter project description'
                  />
                </div>

                <div>
                  <label
                    htmlFor='deadline'
                    className='block text-sm font-medium text-gray-700'
                  >
                    Deadline
                  </label>
                  <input
                    id='deadline'
                    name='deadline'
                    type='date'
                    value={deadline}
                    onChange={(e) => setDeadline(e.target.value)}
                    className='mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm'
                  />
                </div>

                <div className='mt-4 flex justify-end space-x-4'>
                  <BtnSecondary onClick={() => closeModal()}>
                    Cancel
                  </BtnSecondary>
                  <BtnPrimary type='submit'>Save</BtnPrimary>
                </div>
              </form>
            </div>
          </Transition.Child>
        </div>
      </Dialog>
    </Transition>
  );
};

export default memo(AddProjectModal);
