import React, { useEffect, useState, useCallback } from "react";
import axios from "axios";
import toast from "react-hot-toast";

const Navbar = () => {
  const [projects, setProjects] = useState([]);
  const [projectCount, setProjectCount] = useState(0);

  // Fetch projects when component mounts
  useEffect(() => {
    fetchProjects();
  }, []);

  // Fetch projects from API
  const fetchProjects = () => {
    axios
      .get("http://localhost:9000/projects")
      .then((res) => {
        setProjects(res.data);
        setProjectCount(res.data.length);
      })
      .catch((error) => {
        toast.error("Failed to fetch projects");
      });
  };

  // Handle project added event
  const handleProjectAdded = useCallback(() => {
    fetchProjects(); // Refresh projects on new project added
  }, []);

  // Handle project deleted event
  const handleProjectDeleted = useCallback(() => {
    fetchProjects(); // Refresh projects on project deleted
    window.location.reload(); // Reload the page after fetching updated projects
  }, []);

  // Set up event listeners for project events
  useEffect(() => {
    document.addEventListener("projectAdded", handleProjectAdded);
    document.addEventListener("projectDeleted", handleProjectDeleted);

    return () => {
      document.removeEventListener("projectAdded", handleProjectAdded);
      document.removeEventListener("projectDeleted", handleProjectDeleted);
    };
  }, [handleProjectAdded, handleProjectDeleted]); // Include handleProjectAdded and handleProjectDeleted in dependency array

  return (
    <div className='bg-white shadow h-14 flex items-center justify-between px-4'>
      <ul className='flex space-x-4'>
        {projects.map((project) => (
          <li key={project._id}>
            <a
              href={`#${project.title}`}
              className='text-gray-600 cursor-not-allowed'
              tabIndex='-1'
            >
              {project.title}
            </a>
          </li>
        ))}
      </ul>
      
    </div>
  );
};

export default Navbar;
