import React, { useEffect, useState } from "react";
import axios from "axios";
import toast from "react-hot-toast";

const Navbar = () => {
  const [projects, setProjects] = useState([]);
  const [projectsRemaining, setProjectsRemaining] = useState(0);

  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = () => {
    axios
      .get("http://localhost:9000/projects")
      .then((res) => {
        const projectsWithActive = res.data.map((project) => ({
          ...project,
          active: false,
        }));
        setProjects(projectsWithActive);
        setProjectsRemaining(projectsWithActive.length);
      })
      .catch((error) => {
        toast.error("Failed to fetch projects");
      });
  };

  const toggleProject = (projectId) => {
    const updatedProjects = projects.map((project) =>
      project._id === projectId
        ? { ...project, active: !project.active }
        : project
    );
    setProjects(updatedProjects);
    setProjectsRemaining(
      updatedProjects.filter((project) => project.active).length
    );

    document.dispatchEvent(
      new CustomEvent("projectToggled", { detail: { projectId } })
    );
  };

  const handleDelete = (projectId) => {
    axios
      .delete(`http://localhost:9000/projects/${projectId}`)
      .then((res) => {
        setProjects(projects.filter((project) => project._id !== projectId));
        setProjectsRemaining(projectsRemaining - 1);
        toast.success("Project deleted successfully");
      })
      .catch((error) => {
        console.error("Failed to delete project:", error);
        toast.error("Failed to delete project");
      });
  };

  return (
    <div className='bg-white shadow h-14 flex items-center px-4'>
      <ul className='flex space-x-4'>
        <li className='text-blue-700'>
          {projectsRemaining === 0 ? "0" : ` ${projectsRemaining} `}
          {projectsRemaining === 1 ? "task" : "tasks"} remaining
        </li>
        {projects.map((project) => (
          <li
            key={project._id}
            className={`cursor-pointer ${
              project.active ? "font-semibold text-blue-600" : "text-gray-600"
            }`}
            onClick={() => toggleProject(project._id)}
          >
            {project.title}
            <button
              onClick={(e) => {
                e.stopPropagation();
                handleDelete(project._id);
              }}
              className='text-red-600 ml-2 hover:text-red-800 focus:outline-none'
            >
              <svg
                xmlns='http://www.w3.org/2000/svg'
                viewBox='0 0 24 24'
                fill='currentColor'
                className='w-4 h-4'
              >
                <path
                  fillRule='evenodd'
                  d='M3 5.25c0-.69.56-1.25 1.25-1.25h15.5c.69 0 1.25.56 1.25 1.25v1.5H3v-1.5zm3.75 9.5a.75.75 0 011.5 0v5h7.5v-5a.75.75 0 011.5 0v5c0.414-.336.75-.75.75h-9a.75.75 0 01-.75-.75v-5z'
                  clipRule='evenodd'
                />
              </svg>
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Navbar;
