import React, { useState } from "react";
import { Popover } from "@headlessui/react";
import AddTaskModal from "./AddTaskModal";
import axios from "axios";
import toast from "react-hot-toast";

const DropdownMenu = ({ taskId, handleDelete, projectId, setRenderChange }) => {
  const [isEditTaskModalOpen, setEditTaskModal] = useState(false);

  const refreshData = () => {
    setRenderChange(true);
  };

  const handleSetEditModal = (e) => {
    e.stopPropagation();
    setEditTaskModal(true);
  };

  const handleDeleteTask = async (e, taskId) => {
    e.stopPropagation();
    try {
      await axios.delete(`http://localhost:9000/tasks/${taskId}`);
      toast.success("Task deleted successfully");
      refreshData();
    } catch (error) {
      toast.error("Failed to delete task");
    }
  };

  return (
    <>
      <div className='flex space-x-1'>
        <button
          onClick={(e) => handleSetEditModal(e)}
          className='py-1 px-2 right-1.5 top-2 hover:bg-slate-200 rounded-sm focus:outline-none focus:ring focus:ring-indigo-200'
        >
          <svg
            xmlns='http://www.w3.org/2000/svg'
            fill='currentColor'
            width={4}
            className='text-[#9ba8bc]'
            viewBox='0 0 128 512'
          >
            <path d='M64 360c30.9 0 56 25.1 56 56s-25.1 56-56 56s-56-25.1-56-56s25.1-56 56-56zm0-160c30.9 0 56 25.1 56 56s-25.1 56-56 56s-56-25.1-56-56s25.1-56 56-56zM120 96c0 30.9-25.1 56-56 56S8 126.9 8 96S33.1 40 64 40s56 25.1 56 56z' />
          </svg>
        </button>
        <button
          onClick={(e) => handleDeleteTask(e, taskId)}
          className='py-1 px-2 right-1.5 top-2 hover:bg-slate-200 rounded-sm focus:outline-none focus:ring focus:ring-indigo-200'
        >
          <svg
            xmlns='http://www.w3.org/2000/svg'
            fill='currentColor'
            width={4}
            className='text-[#9ba8bc]'
            viewBox='0 0 448 512'
          >
            <path d='M135.2 17.7C140.6 6.8 151.7 0 163.8 0H284.2c12.1 0 23.2 6.8 28.6 17.7L320 32h96c17.7 0 32 14.3 32 32s-14.3 32-32 32H32C14.3 96 0 81.7 0 64S14.3 32 32 32h96l7.2-14.3zM32 128H416V448c0 35.3-28.7 64-64 64H96c-35.3 0-64-28.7-64-64V128zm96 64c-8.8 0-16 7.2-16 16V432c0 8.8 7.2 16 16 16s16-7.2 16-16V208c0-8.8-7.2-16-16-16zm96 0c-8.8 0-16 7.2-16 16V432c0 8.8 7.2 16 16 16s16-7.2 16-16V208c0-8.8-7.2-16-16-16zm96 0c-8.8 0-16 7.2-16 16V432c0 8.8 7.2 16 16 16s16-7.2 16-16V208c0-8.8-7.2-16-16-16z' />
          </svg>
        </button>
      </div>
      <AddTaskModal
        isAddTaskModalOpen={isEditTaskModalOpen}
        setAddTaskModal={setEditTaskModal}
        projectId={projectId}
        taskId={taskId}
        edit={true}
        refreshData={refreshData}
      />
    </>
  );
};

export default DropdownMenu;
