// ProjectContext.js

import React, { createContext, useState, useContext } from "react";

// Create a context for managing projects
const ProjectContext = createContext();

// Create a provider component to manage projects state
export const ProjectProvider = ({ children }) => {
  const [projects, setProjects] = useState([]);

  // Example functions to manage projects
  const addProject = (project) => {
    setProjects([...projects, project]);
  };

  const updateProject = (projectId, updatedProject) => {
    const updatedProjects = projects.map((proj) =>
      proj._id === projectId ? { ...proj, ...updatedProject } : proj
    );
    setProjects(updatedProjects);
  };

  // Context value to provide
  const contextValue = {
    projects,
    addProject,
    updateProject,
  };

  return (
    <ProjectContext.Provider value={contextValue}>
      {children}
    </ProjectContext.Provider>
  );
};

// Custom hook to consume the ProjectContext
export const useProjectContext = () => {
  return useContext(ProjectContext);
};
