import React, { useState, useEffect } from "react";
import axios from "axios";
import toast from "react-hot-toast";

const Navbar = () => {
  const [projects, setProjects] = useState([]);
  const [projectsRemaining, setProjectsRemaining] = useState(0);
  const [newProject, setNewProject] = useState(""); // Add a state for the new project title

  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = () => {
    axios
      .get("http://localhost:9000/projects")
      .then((res) => {
        setProjects(res.data);
        setProjectsRemaining(res.data.length);
      })
      .catch((error) => {
        toast.error("Failed to fetch projects");
      });
  };

  const handleDelete = (projectId) => {
    axios
      .delete(`http://localhost:9000/projects/${projectId}`)
      .then((res) => {
        setProjects(projects.filter((project) => project._id !== projectId));
        setProjectsRemaining(projectsRemaining - 1);
        toast.success("Project deleted successfully");
        // Dispatch an event to refresh the sidebar
        document.dispatchEvent(new CustomEvent("projectsUpdated"));
      })
      .catch((error) => {
        console.error("Failed to delete project:", error);
        toast.error("Failed to delete project");
      });
  };

  const handleAddProject = (newProject) => {
    axios
      .post("http://localhost:9000/projects", newProject)
      .then((res) => {
        setProjects([...projects, res.data]);
        setProjectsRemaining(projectsRemaining + 1);
        toast.success("Project added successfully");
        // Dispatch an event to refresh the sidebar
        document.dispatchEvent(new CustomEvent("projectsUpdated"));
      })
      .catch((error) => {
        console.error("Failed to add project:", error);
        toast.error("Failed to add project");
      });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (newProject.trim() !== "") {
      handleAddProject({ title: newProject }); // Call the handleAddProject function
      setNewProject(""); // Reset the new project title
    }
  };

  return (
    <div className='bg-white shadow h-14 flex items-center px-4'>
      <ul className='flex space-x-4'>
        <li className='text-blue-700'>
          {projectsRemaining === 0 ? "0" : ` ${projectsRemaining} `}
          {projectsRemaining === 1 ? "task" : "tasks"} remaining
        </li>
        {projects.map((project) => (
          <li key={project._id} className='text-gray-600'>
            {project.title}
            <button
              onClick={(e) => {
                e.stopPropagation();
                handleDelete(project._id);
              }}
              className='text-red-600 ml-2 hover:text-red-800 focus:outline-none'
            >
              <svg
                xmlns='http://www.w3.org/2000/svg'
                viewBox='0 0 24 24'
                fill='currentColor'
                className='w-4 h-4'
              >
                <path
                  fillRule='evenodd'
                  d='M3 5.25c0-.69.56-1.25 1.25-1.25h15.5c.69 0 1.25.56 1.25 1.25v1.5H3v-1.5zm3.75 9.5a.75.75 0 011.5 0v5h7.5v-5a.75.75 0 011.5 0v5c0.414-.336.75-.75.75h-9a.75.75 0 01-.75-.75v-5z'
                  clipRule='evenodd'
                />
              </svg>
            </button>
          </li>
        ))}
      </ul>
      <form onSubmit={handleSubmit}>
        <input
          type='text'
          value={newProject}
          onChange={(e) => setNewProject(e.target.value)}
          placeholder='Add new project'
          className='bg-gray-100 p-2 rounded'
        />
        <button
          type='submit'
          className='bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded'
        >
          Add Project
        </button>
      </form>
    </div>
  );
};

export default Navbar;
