import React, { useState, useEffect } from "react";
import axios from "axios";
import toast from "react-hot-toast";

const Navbar = () => {
  const [projectsRemaining, setProjectsRemaining] = useState(0);

  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = () => {
    axios
      .get("http://localhost:9000/Projects")
      .then((res) => {
        setProjectsRemaining(res.data.length);
      })
      .catch((error) => {
        toast.error("Failed to fetch projects");
      });
  };

  return (
    <div className='bg-white shadow h-14 flex items-center px-4'>
      <span className='text-gray-600'>Projects: {projectsRemaining}</span>
    </div>
  );
};

export default Navbar;
