import React, { createContext, useState, useEffect } from "react";
import axios from "axios";
import toast from "react-hot-toast";

const ProjectContext = createContext();

const ProjectProvider = ({ children }) => {
  const [projects, setProjects] = useState([]);

  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = () => {
    axios
      .get("http://localhost:9000/projects")
      .then((res) => {
        setProjects(res.data);
      })
      .catch((error) => {
        toast.error("Failed to fetch projects");
      });
  };

  const addProject = (newProject) => {
    axios
      .post("http://localhost:9000/projects", newProject)
      .then((res) => {
        setProjects([...projects, res.data]); // Add new project to state
        toast.success("Project added successfully");
        document.dispatchEvent(new Event("projectAdded")); // Dispatch event to notify listeners
      })
      .catch((error) => {
        toast.error("Failed to add project");
      });
  };

  return (
    <ProjectContext.Provider value={{ projects, addProject }}>
      {children}
    </ProjectContext.Provider>
  );
};

export { ProjectContext, ProjectProvider };
