import React, { useState, useEffect } from "react";

const Sidebar = () => {
  const [projects, setProjects] = useState([]);

  useEffect(() => {
    document.addEventListener("projectsUpdated", fetchProjects);
    fetchProjects();
    return () => {
      document.removeEventListener("projectsUpdated", fetchProjects);
    };
  }, []);

  const fetchProjects = () => {
    // Fetch projects from API or local storage
    // Update the state with the new projects
    setProjects([...projects]);
  };

  return (
    <div className='bg-gray-100 h-screen p-4'>
      <h2 className='text-lg font-bold'>Projects</h2>
      <ul>
        {projects.map((project) => (
          <li key={project._id} className='py-2'>
            {project.title}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Sidebar;
