import React, { useEffect, useState } from "react";
import axios from "axios";
import toast from "react-hot-toast";

const Navbar = () => {
  const [projects, setProjects] = useState([]);

  useEffect(() => {
    const fetchProjects = () => {
      axios
        .get("http://localhost:9000/projects")
        .then((res) => {
          setProjects(res.data);
        })
        .catch((error) => {
          toast.error("Failed to fetch projects");
        });
    };

    fetchProjects();

    const handleProjectAdded = () => {
      fetchProjects(); // Refresh projects on new project added
    };

    document.addEventListener("projectAdded", handleProjectAdded);

    return () => {
      document.removeEventListener("projectAdded", handleProjectAdded);
    };
  }, []); // Empty dependency array ensures this effect runs only once on mount

  const toggleProject = (projectId) => {
    // Toggle the 'active' state of the project with projectId
    const updatedProjects = projects.map((project) =>
      project._id === projectId
        ? { ...project, active: !project.active }
        : project
    );
    setProjects(updatedProjects);
  };

  return (
    <div className='bg-white shadow h-14 flex items-center px-4'>
      <ul className='flex space-x-4'>
        {projects.map((project) => (
          <li
            key={project._id}
            className={`cursor-pointer ${
              project.active ? "font-semibold text-blue-600" : "text-gray-600"
            }`}
            onClick={() => toggleProject(project._id)}
          >
            {project.title}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Navbar;
