import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";

const Sidebar = () => {
  const [projects, setProjects] = useState([]);

  const fetchProjects = useCallback(() => {
    // Fetch projects from API or local storage
    // Update the state with the new projects
    axios
      .get("http://localhost:9000/projects")
      .then((res) => {
        setProjects(res.data);
      })
      .catch((error) => {
        console.error("Failed to fetch projects:", error);
      });
  }, [setProjects]);

  useEffect(() => {
    document.addEventListener("projectsUpdated", fetchProjects);
    fetchProjects();
    return () => {
      document.removeEventListener("projectsUpdated", fetchProjects);
    };
  }, [fetchProjects]);

  return (
    <div className='bg-gray-100 h-screen p-4'>
      <h2 className='text-lg font-bold'>Projects</h2>
      <ul>
        {projects.map((project) => (
          <li key={project._id} className='py-2'>
            {project.title}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Sidebar;
