import React, { useState, useEffect } from "react";
import axios from "axios";
import toast from "react-hot-toast";

const Navbar = () => {
  const [projects, setProjects] = useState([]);
  const [projectsRemaining, setProjectsRemaining] = useState(0);

  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = () => {
    axios
      .get("http://localhost:9000/projects")
      .then((res) => {
        setProjects(res.data);
        setProjectsRemaining(res.data.length);
      })
      .catch((error) => {
        toast.error("Failed to fetch projects");
      });
  };

  const handleDeleteProject = (id) => {
    axios
      .delete(`http://localhost:9000/project/${id}`)
      .then((res) => {
        toast.success("Project deleted successfully");
        fetchProjects();
      })
      .catch((error) => {
        toast.error("Failed to delete project");
      });
  };

  return (
    <div className='bg-white shadow h-14 flex items-center px-4'>
      <span className='text-gray-600'>
        Projects: {projectsRemaining} (
        {projects.map((project) => project.title).join(", ")})
      </span>
      <ul className='flex flex-wrap justify-end ml-4'>
        {projects.map((project) => (
          <li key={project.id} className='mr-4'>
            <a href='#' className='text-gray-600 hover:text-gray-900'>
              {project.title}
            </a>
            <button
              onClick={() => handleDeleteProject(project.id)}
              className='text-red-600 hover:text-red-900'
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Navbar;
