  import React, { useCallback, useEffect, useState } from "react";
  import axios from "axios";
  import { Link } from "react-router-dom";
  import AddProjectModal from "./AddProjectModal";

  const Sidebar = () => {
    const [isModalOpen, setModalState] = useState(false);
    const [projects, setProjects] = useState([]);
    const [paramsWindow, setParamsWindow] = useState(
      window.location.pathname.slice(1)
    );

    useEffect(() => {
      fetchProjects();
    }, []);

    const fetchProjects = useCallback(() => {
      axios
        .get("http://localhost:9000/projects/")
        .then((res) => {
          setProjects(res.data);
        })
        .catch((error) => {
          console.error("Failed to fetch projects:", error);
          // Handle error, e.g., toast.error('Failed to fetch projects');
        });
    }, []);

    const handleDelete = (projectId) => {
      axios
        .delete(`http://localhost:9000/projects/${projectId}`)
        .then((res) => {
          // Remove the deleted project from state
          setProjects(projects.filter((project) => project._id !== projectId));
          // Optionally show a success message
          // toast.success('Project deleted successfully');
        })
        .catch((error) => {
          console.error("Failed to delete project:", error);
          // Handle error, e.g., toast.error('Failed to delete project');
        });
    };

    const handleLocation = (e) => {
      setParamsWindow(new URL(e.currentTarget.href).pathname.slice(1));
    };

    const openModal = useCallback(() => {
      setModalState(true);
    }, []);

    const closeModal = useCallback(() => {
      setModalState(false);
    }, []);

    return (
      <div className='py-5'>
        <div className='px-4 mb-3 flex items-center justify-between'>
          <span className=''>Projects</span>
          <button
            onClick={openModal}
            className='bg-indigo-200 rounded-full p-[2px] focus:outline-none focus:ring focus:ring-indigo-200 focus:ring-offset-1'
          >
            <svg
              xmlns='http://www.w3.org/2000/svg'
              viewBox='0 0 24 24'
              fill='currentColor'
              className='w-5 h-5 text-indigo-600'
            >
              <path
                fillRule='evenodd'
                d='M12 5.25a.75.75 0 01.75.75v5.25H18a.75.75 0 010 1.5h-5.25V18a.75.75 0 01-1.5 0v-5.25H6a.75.75 0 010-1.5h5.25V6a.75.75 0 01.75-.75z'
                clipRule='evenodd'
              />
            </svg>
          </button>
        </div>
        <ul className='border-r border-gray-300 pr-2'>
          {projects.map((project, index) => (
            <li
              key={project._id}
              className={`px-5 py-1.5 mb-1 text-gray-600 font text-sm capitalize select-none hover:text-indigo-600 rounded transition-colors hover:bg-indigo-200/80 ${
                paramsWindow === project._id && "text-indigo-600 bg-indigo-200/80"
              }`}
            >
              <Link to={`/${project._id}`} onClick={handleLocation}>
                <div className='flex justify-between items-center'>
                  <span>{project.title}</span>
                  <span className='text-gray-500'>
                    {new Date(project.deadline).toLocaleDateString("en-GB", {
                      year: "numeric",
                      month: "short",
                      day: "numeric",
                    })}
                  </span>
                  <button
                    onClick={(e) => {
                      e.stopPropagation();
                      handleDelete(project._id);
                    }}
                    className='text-red-600 ml-2 hover:text-red-800 focus:outline-none'
                  >
                    <svg
                      xmlns='http://www.w3.org/2000/svg'
                      viewBox='0 0 24 24'
                      fill='currentColor'
                      className='w-4 h-4'
                    >
                      <path
                        fillRule='evenodd'
                        d='M3 5.25c0-.69.56-1.25 1.25-1.25h15.5c.69 0 1.25.56 1.25 1.25v1.5H3v-1.5zm3.75 9.5a.75.75 0 011.5 0v5h7.5v-5a.75.75 0 011.5 0v5c0.414-.336.75-.75.75h-9a.75.75 0 01-.75-.75v-5z'
                        clipRule='evenodd'
                      />
                    </svg>
                  </button>
                </div>
              </Link>
            </li>
          ))}
        </ul>
        <AddProjectModal isModalOpen={isModalOpen} closeModal={closeModal} />
      </div>
    );
  };

  export default Sidebar;
